mapper.update:

	UPDATE PRODUCTS_TB SET CTGY_PTCD=#{ctgyPtcd}, CTGY_PTNM=#{ctgyPtnm}, CTGY_CD=#{ctgyCd}, CTGY_NM=#{ctgyNm}, 
	PD_NM=#{pdNm}, PD_TAG=#{pdTag}, PD_SALE=#{pdSale}, PD_STATUS=#{pdStatus}, PD_IMG=#{pdImg}, PD_DETL=#{pdDetl}, PD_STOCK=#{pdStock}, PD_UPDDT=SYSDATE
	WHERE PD_NO=#{pdNo}


	UPDATE PRODUCTS_TB SET
	PD_TAG=#{pdTag}, PD_SALE=#{pdSale}, PD_STATUS=#{pdStatus}, PD_STOCK=#{pdStock}
	WHERE PD_NO=#{pdNo}
